// for button
document.addEventListener('click', (evt) => {
  const { target, path } = evt;
  let btnTarget = null;

  if (target && target.classList && target.classList.contains('next-btn')) {
    btnTarget = target;
  } else {
    path.some(item => {
      if (item && item.classList && item.classList.contains('next-btn')) {
        btnTarget = item;
        return true;
      } else {
        return false;
      }
    });
  }

  if (btnTarget) {
    btnTarget.classList.add('btn-animate');
    setTimeout(() => {
      btnTarget.classList.remove('btn-animate');
    }, 400);
  }
});